import { Plugin } from 'vite';

interface VitePluginStripOptions {
    /**
     * 是否启用插件所有功能
     * @default true
     */
    enabled?: boolean;
    /**
     * 判断 location.host 是否在 domainList 中，如果是，它将覆盖默认的 console.log 函数，在调用时不执行任何操作
     * @default []
     */
    domainList?: string[];
    /**
     * 开始标记
     * @default 'devBlock:start'
     * @example
     *
     * ```ts
     * // devBlock:start
     * console.log('start')
     * // devBlock:end
     * ```
     */
    start?: string;
    /**
     * 结束标记
     * @default 'devBlock:end'
     */
    end?: string;
    enable?: () => void;
}
declare const stripPlugin: (options: VitePluginStripOptions) => Plugin;

export { stripPlugin as default };
